	import { onMount } from 'svelte';
	import BackgroundSlider from '$lib/components/BackgroundSlider.svelte';
	import Slide from '$lib/components/Slide.svelte';
	import gsap from 'gsap';
	import { replayAnimations } from '$lib/components/animationStore.js';

	let activeStates = {
		Dev: true,
		Des: false,
		Me: false
	};

	function pageSwapAnimation(element, setState) {
		let tl = gsap
			.timeline()
			.set('.rest', {
				display: 'none'
			})
			.set(setState === 'Dev' ? '.dev-section' : '.des-section', { display: 'block' });
		tl.fromTo(
			element,
			{ zIndex: 100, y: '100%', position: 'fixed', top: '0', left: '0' },
			{ y: '0%', duration: 0.8, ease: 'power2.out' }
		)
			.set(
				element,
				{
					zIndex: '',
					position: '',
					top: '',
					left: ''
				},
				'+=1'
			)
			.set('.rest', {
				display: 'block'
			});
		tl.set(setState === 'Dev' ? '.des-section' : '.dev-section', { display: 'none' });
	}

	function getTextClass(target) {
		return activeStates[target] ? 'text-orange' : '';
	}

	function toggleState(setState) {
		if (setState === 'Me') {
			activeStates.Me = !activeStates.Me;
		} else {
			const otherState = setState === 'Dev' ? 'Des' : 'Dev';
			if (!activeStates[otherState]) {
				return;
			}
			let element = document.querySelector(setState === 'Dev' ? '.dev-slide' : '.des-slide');
			replayAnimations(setState);
			pageSwapAnimation(element, setState);

			activeStates[setState] = true;
			setTimeout(() => {
				activeStates[otherState] = false;
			}, 1000);
		}
	}

	let x = 0;
	let y = 0;
	let ballSize = 24;
	let maxSpeed = 0.05;
	let targetX = 0;
	let targetY = 0;

	function handleMouseMove(event) {
		targetX = event.clientX;
		targetY = event.clientY;
	}

	onMount(() => {
		function animate() {
			let dx = targetX - x;
			let dy = targetY - y;
			x += dx * maxSpeed;
			y += dy * maxSpeed;

			requestAnimationFrame(animate);
		}

		animate();

		return () => {};
	});
</script>

